const FormReducer = (state, action) => {
    switch (action.type) {
        case "OPEN_MENU":
            return { showForm: true }
            break;
        case "CLOSE_MENU":
            return { showForm: false }
            break
        default:
            return state;
    }

}

export default FormReducer;
