import { createContext, useReducer } from "react"
import FormReducer from "./FormReducer";

const showForm = true

export const FormContext = createContext(showForm)

export const FormContextProvider = ({ children }) => {

    const [state, dispatch] = useReducer(FormReducer, showForm);



    return (
        <FormContext.Provider
            value={{ showForm, dispatch }}
        >
            {children}
        </FormContext.Provider >
    );
};
